                <span class="token keyword">if</span> <span class="token punctuation">(</span><span class="token punctuation">(</span>image <span class="token operator">=</span> webcam<span class="token punctuation">.</span><span class="token function">getImage</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token operator">==</span> <span class="token keyword">null</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
